async function fetchData(e){var t;return await fetch(e).then(e=>e.json()).then(e=>t=e),t}async function processPayment(){fetchData("http://localhost:5000/pay/"+document.getElementById("payment_user").value+"/"+document.getElementById("payment_merchant").value+"/"+document.getElementById("payment_amount").value).then(e=>{e="User Balance: "+(e.user_details.balance+" "+e.user_details.country)+"<br>Merchant Balance: "+(e.merchant_details.balance+" "+e.merchant_details.country)+"<br>Exchange Rate: "+e.exchange_rate;$(".modal-title").html("Transaction Details"),$(".modal-body").html(e),$("#myModal").modal(),processBalances()})}async function processBalances(){fetchData("http://localhost:5000/get_users").then(e=>{for(var t in e)document.getElementById(t).innerHTML=e[t].country+" "+e[t].balance})}async function centralBankTopup(){fetchData("http://localhost:5000/central_bank/topup/"+document.getElementById("top_up_select_value").value+"/"+document.getElementById("top_up_amount").value).then(e=>{alert(e.status)})}